def play(coorList):
    pos = 228
    p.moveTo(coorList[pos])

    while True:
        if k.is_pressed("w") and pos-24>-1:
            pos -= 24
            p.moveTo(coorList[pos])
            sleep(.05)
            x,y = p.position()
            print(p.pixel(x+1,y))
        if k.is_pressed("s") and pos+24<480:
            pos += 24
            p.moveTo(coorList[pos])
            sleep(.05)
            x,y = p.position()
            print(p.pixel(x+1,y)) 
        if k.is_pressed("d") and pos+1<480 and (pos+1)//24 == pos//24:
            pos += 1
            p.moveTo(coorList[pos])
            sleep(.05)
            x,y = p.position()
            print(p.pixel(x+1,y))
        if k.is_pressed("a") and pos-1<480 and (pos-1)//24 == pos//24:
            pos -= 1
            p.moveTo(coorList[pos])
            sleep(.05)
            x,y = p.position()
            print(p.pixel(x+1,y))
        if k.is_pressed("="):
            break
        if k.is_pressed("i"):
            p.click()
            sleep(.05)
        if k.is_pressed("o"):
            p.click(button = "right")
            sleep(.05)

def updateBoard(n):
    global currNums
    global board
    global known
    colors = [
        (25, 118, 210), #blue(1)
        (56, 142, 60), #green(2)
        (211, 47, 47), #red(3)
        (123, 31, 162), #purple(4)
        (255, 143, 1), #orange(5)
        (85, 172, 173), #teal(6)
        (229, 194, 159), #lighter background
        (215, 184, 153), #darker background
        (170, 215, 81), #lighter green
        (162, 209, 73) #darker green
    ]
    imgWhole = ""
    if n == 1:
        imgWhole = ImageGrab.grab(bbox = (652, 355, 1251, 854)) #big
    else:
        imgWhole = ImageGrab.grab(bbox = (498,263,1397,1012)) #big

    imgData = imgWhole.load()

    currKnown = set()
    for i in range(480):
        if i not in known:
            val = ""
            x = i%24
            y = i//24
            cropimg = ""
            if n == 1:
                cropimg = imgWhole.crop((x*25,y*25,(x+1)*25,(y+1)*25)) #big 
                #img.show()
            else:
                cropimg = imgWhole.crop((x*37.5,y*37.5,(x+1)*37.5,(y+1)*37.5)) #small
            cropimg.load()
            imgColors = cropimg.convert('RGB').getcolors()
            for m in range(6):
                if str(colors[m]) in str(imgColors):
                    val = str(m+1)
                    currNums[i] = val
                    currKnown.add(i)
                    break
            if val == "":
                if n == 1: 
                    color = imgData[x*25+12.5,y*25+7]
                    if color == (170, 215, 81) or color == (162, 209, 73):
                        val = "X"
                    elif color == (229, 194, 159) or color == (215, 184, 153):
                        val = "-"
                else:
                    color = imgData[x*37.5+18.75,y*37.5+18.75]
                    if color == (170, 215, 81) or color == (162, 209, 73):
                        val = "X"
                    elif color == (229, 194, 159) or color == (215, 184, 153):
                        val = "-"
            if val == "-":
                currKnown.add(i)
            if i >= len(board):
                board.append(val)
            else:
                board[i] = val
    for k in currKnown:
        known.add(k)

